const uniqueArray = arr => { 
  const seen = {}; 
  const output = []; 

  for (let i = 0; i < arr.length; i++) { 
    if (!seen[arr[i]]) { 
      seen[arr[i]] = true; 
      output.push(arr[i]); 
    } 
  } 
  return output; 
} 

